module.exports = function (RED) {
	function NodeNavigateBrowser(conf) {
		RED.nodes.createNode(this, conf)

		let node = this

		node.on('input', async (message, send, done) => {
			// Cheat to allow correct typing in typescript
			const msg = message

			if (!msg.context || !msg.browser) {
				node.error('Browser instance not found!')
				node.status({ fill: 'yellow', shape: 'dot', text: 'instance not found' })
				done()
			}

			try {
				await msg.page.goto(conf.webURL)

				if (conf.isVisible) {
					await msg.page.isVisible(conf.isVisible)
				}

				await msg.page.waitForTimeout(conf.timeout)

				node.status({ fill: 'green', shape: 'dot', text: 'success' })

				send(msg)
				done()
			} catch (e) {
				msg.browser = null
				msg.context = null
				node.error('Error navigate browser!')
				node.status({ fill: 'red', shape: 'ring', text: 'navigate error' })
				done(e)
			}
		})
	}

	RED.nodes.registerType('navigate-browser', NodeNavigateBrowser)
}
