module.exports = function (RED) {
	function NodeScreenshotBrowser(conf) {
		RED.nodes.createNode(this, conf)

		let node = this

		node.on('input', async (message, send, done) => {
			// Cheat to allow correct typing in typescript
			const msg = message

			if (!msg.context || !msg.browser) {
				node.error('Browser instance not found!')
				node.status({ fill: 'yellow', shape: 'dot', text: 'instance not found' })
				done()
			}

			try {
				const buffer = await msg.page.screenshot()

				msg.screenshot = buffer.toString('base64')

				node.status({ fill: 'green', shape: 'dot', text: 'success' })

				send(msg)
				done()
			} catch (e) {
				msg.browser = null
				msg.context = null
				node.error('Error screenshot browser!')
				node.status({ fill: 'red', shape: 'ring', text: 'screenshot error' })
				done(e)
			}
		})
	}

	RED.nodes.registerType('screenshot-browser', NodeScreenshotBrowser)
}
